import React, { useState, useEffect } from 'react'
import axios from 'axios'

function Modal({ativo}) {

   const [data, setData] = useState([])
   useEffect(() => {
      axios.get('http://localhost:8000/api/crud/fornecedoras')
         .then(response => {
            setData(response.data)
         })
         .catch(error => {
            return error
         })
   }, [])

   return (
      <div className="fundoModal">
         <div className="addModal">

            <button className="fecharModal" onClick={set}>Cancelar</button>

            <form className="formModal">
               <input type="text" name="nome" placeholder="Nome" />
               <input type="text" name="descricao" placeholder="Descrição" />
               <input type="number" name="valor" placeholder="Valor" />

               <select name="fornecedora">
                  <option value="" disabled selected>Fornecedora</option>
                  {data.map(fornecedora => (
                     <option key={fornecedora.id} value={fornecedora.id}>
                        {fornecedora.nome}
                     </option>
                  ))}
               </select>

               <button className="btnFormModal" type='submit'>Cadastrar Produto</button>

            </form>
         </div>
      </div>
   )
}

export default Modal
